{let t=function(){let t=$("#new-post-form");t.submit((function(o){o.preventDefault(),$.ajax({type:"post",url:"/posts/create",data:t.serialize(),success:function(t){let o=n(t.data);$("#posts-list-container>ul").prepend(o),e($(" .delete-post-button",o))},error:function(t){console.log(t.responseText)}})}))},n=function(t){return $(`<li id="post-${t.post._id}">\n                    <p>\n                        ${t.post.content} <br>\n                        <small>\n                            ${t.username}\n                        </small>\n                \n                        \n                        <small>\n                            <a class="delete-post-button" href="/posts/destroy/${t.post._id}">Delete</a>\n                        </small>\n                        \n                    </p>\n                    <div class="post-comments">\n                        \n                            <form action="/comments/create" method="POST">\n                                <input type="text" name="content" placeholder="Type here to add comment..." required>\n                                <input type="hidden" name="post" value="${t.post._id}">\n                                <input type="submit" value="Add Comment">\n                            </form>\n                       \n                \n                        <div class="post-comments-list">\n                            <ul id="post-comments-${t.post._id}">\n                                \n                            </ul>\n                        </div>\n                    </div>\n                </li>`)},e=function(t){$(t).click((function(n){n.preventDefault(),$.ajax({type:"get",url:$(t).prop("href"),success:function(t){$(`#post-${t.data.post_id}`).remove()},error:function(t){console.log(t.responseText)}})}))};t()}